import request from "./request";
export async function sendPostRequest(params) {
  return request(`/api/getFiles`, {
    method: 'POST',
    data: params,
  }).then(response => {    
    if (response && response.errors)
      response.errors.map(({ message: msg }) => alert(msg));
    else if (response && !response.errors) {
      return response;
    }
  });
}
