/*
    该文件是用于创建一个为Count服务的reducer，reducer的本质就是一个函数
    reducer函数会接到两个参数，分别为：之前的状态（preState），动作对象（action）
*/
import { FETCHMENU } from "./constant"
const initState = 0
const routerData = ''
const state = {
    menuData: [],
    routerData: '',
    menuList: [],
}

export default function countReducer(preState = routerData, action) {
    // 从action对象中获取：type、data
    const { type, data } = action
    console.log('====================================');
    console.log(type,'type');
    console.log('====================================');
    // 根据type决定如何加工数据
    switch (type) {
        case FETCHMENU: //如果是加
            return preState = data
        default:
            return preState
    }
}
